import { taskUseCase } from '$/domain/useCase/taskUseCase';
import { z } from 'zod';
import { defineController, multipartFileValidator } from './$relay';

export default defineController(() => ({
  post: {
    validators: {
      body: z.object({ label: z.string(), image: multipartFileValidator().optional() }),
    },
    handler: async ({ user, body }) => ({
      status: 201,
      body: await taskUseCase.create(user, body.label, body.image),
    }),
  },
  patch: {
    validators: {
      body: z.object({
        taskId: z.string(),
        done: z.boolean(),
        label: z.string(),
      }),
    },
    handler: async ({ user, body }) => {
      const task = await taskUseCase.update(user, body.taskId, body.done, body.label);

      return { status: 204, body: task };
    },
  },
  delete: async ({ user, body }) => {
    await taskUseCase.delete(user, body.taskId);

    return { status: 204 };
  },
}));

import type { TaskModel } from '$/api/@types/models';
import type { DefineMethods } from 'aspida';

export type Methods = DefineMethods<{
  post: {
    reqFormat: FormData;
    reqBody: {
      label: string;
      image?: Blob;
    };
    resBody: TaskModel;
  };

  patch: {
    reqBody: {
      taskId: string;
      done: boolean;
      label: string;
    };
    status: 204;
    resBody: TaskModel;
  };

  delete: {
    reqBody: {
      taskId: string;
    };
    status: 204;
  };
}>;

import { taskRepo } from '$/domain/repository/taskRepo';
import { prismaClient } from '$/service/prismaClient';
import { defineController } from './$relay';

export default defineController(() => ({
  get: async ({ query }) => ({
    status: 200,
    body: await taskRepo.findAll(prismaClient, query?.limit),
  }),
}));

import type { TaskModel } from '$/api/@types/models';
import type { DefineMethods } from 'aspida';

export type Methods = DefineMethods<{
  get: {
    query?: {
      limit?: number;
    };
    resBody: TaskModel[];
  };
}>;
